import { Component, OnInit } from '@angular/core';
import { ListService } from '../list.service';
@Component({
  selector: 'app-shopping-list',
  templateUrl: './shopping-list.component.html',
  styleUrls: ['./shopping-list.component.css']
})
export class ShoppingListComponent implements OnInit{

public url: any;
selectFile(event: any) { 

  var reader = new FileReader();
  reader.readAsDataURL(event.target.files[0]);
  reader.onload = (_event) => {
    this.url = reader.result; 
  }
}

  public name:any;
  public ingredient:any;
  public desc:any;
  public recipelist:any=[];
  public itemlist:any=[];
  constructor(private recipeservice:ListService){}

getlistservice(){

  this.recipeservice.name=this.name;
  this.recipeservice.ingredient=this.ingredient;
  this.recipeservice.description=this.desc;
  this.recipeservice.url=this.url;
  this.recipelist=this.recipeservice.add();
  this.itemlist=this.recipeservice.list;
  }

ngOnInit(): void {
this.getlistservice()
  }
}
